import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import * as S from './Styled.js';
import Header from '../Header/Header.js';
import './style.css';
import { useSelector } from 'react-redux';

const Myinfo = () => {
    const navigate = useNavigate();
    //const profdet = document.querySelector(".profdet-li");
    const [isSelect, setToggle] = useState(false);
    
    function liCheck() {
        setToggle(isSelect => !isSelect);   // on/off (boolean)
    }
    

    return (
        <>
            <S.Container>
                <Header />
                <S.MyContent>
                    <S.Prof>
                        <div className='userPrf'>
                            <img className='userPrf-prf' src='img/user-1.png' width='70' alt='프로필 사진' />
                        </div>
                        <S.ProfName><span>{localStorage.getItem('username')}</span></S.ProfName>
                    </S.Prof>
                    <S.ProfDetail>
                        <S.ProfDetailLi>
                            <ul>
                                <li className="profdet-li" onClick={() => {navigate("/myinfo/info")}}>내 정보</li>
                                <li className="profdet-li">장바구니</li>
                                <li className="profdet-li">찜한 상품</li>
                                <li className="profdet-li">내Q&A</li>
                            </ul>
                        </S.ProfDetailLi>

                    </S.ProfDetail>
                </S.MyContent>
            </S.Container>
        </>
    );
};

// 내 정보(main), 장바구니, 찜한 상품, 내Q&A 

export default Myinfo;
